package wee1.day2;

public class PositiveToNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = -40;
		
		if (num<0) {
			num=num*-1;
			System.out.println("The Number is converted to Positive: " +num);
		}
		
		else {
			System.out.println("The Number is Positive");
		}
	}

}
